    FeatureStyle.fromColorToString = function(color) {
        var hashColor = "#";
        for (var i = 0; i < 3; i++) {
            var component = parseInt(color[i] * 255.0, 10).toString(16);
            hashColor += component < 10 ? "0" + component : component;
        }

        return hashColor;
    };

