        var longInRad = (geoPos[0] * Math.PI) / 180; // longitude
        var latInRad = (geoPos[1] * Math.PI) / 180; // latitude

        var x = longInRad - (this.lambda0 * Math.PI) / 180;
        var y = Math.log(Math.tan(latInRad) + 1 / Math.cos(latInRad));

        dest[0] = x;
        dest[1] = y;
        dest[2] = geoPos[2];
