            var fragmentLabelShader = "#ifdef GL_ES \n";
            fragmentLabelShader += "precision highp float; \n";
            fragmentLabelShader += "#endif \n";
            fragmentLabelShader += "\n";
            fragmentLabelShader += "varying vec2 texCoord; \n";
