        ctx.setTime(date);

        var theColorRewind =
            $("#objectRewind:hover svg").css("fill") === "rgb(255, 0, 0)"
                ? "red"
                : "white";
        var theColorForward =
            $("#objectForward:hover svg").css("fill") === "rgb(255, 0, 0)"
